            $scope.pager.lastItem = $scope.pager.totalItems < $scope.pager.perPage ?  $scope.pager.totalItems : ($scope.pager.page * $scope.pager.perPage);



            var numPages =  Math.floor($scope.pager.totalItems / $scope.pager.maxSize);
